mapper.select:
select 

from area_goods
where area_id = #{areaId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}

mapper.delete:
delete from area_goods
where area_id = #{areaId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}

delete from area_goods
where area_id = #{id,jdbcType=INTEGER}
and last_updated_date = #{lastUpdatedDate,jdbcType=DATE}
and last_updated_by = #{lastUpdatedBy,jdbcType=VARCHAR}
  and goods_id = #{id,jdbcType=INTEGER}
and last_updated_date = #{lastUpdatedDate,jdbcType=DATE}
and last_updated_by = #{lastUpdatedBy,jdbcType=VARCHAR}

mapper.insert:
insert into area_goods (area_id, goods_id, is_active, 
  inactive_date, created_by, created_date, 
  last_updated_by, last_updated_date, row_id
  )
values (#{areaId,jdbcType=INTEGER}, #{goodsId,jdbcType=INTEGER}, #{isActive,jdbcType=BIT}, 
  #{inactiveDate,jdbcType=DATE}, #{createdBy,jdbcType=VARCHAR}, #{createdDate,jdbcType=DATE}, 
  #{lastUpdatedBy,jdbcType=VARCHAR}, #{lastUpdatedDate,jdbcType=DATE}, #{rowId,jdbcType=VARCHAR}
  )

mapper.update:
update area_goods


    is_active = #{isActive,jdbcType=BIT},


    inactive_date = #{inactiveDate,jdbcType=DATE},


    created_by = #{createdBy,jdbcType=VARCHAR},


    created_date = #{createdDate,jdbcType=DATE},


    last_updated_by = #{currentUser,jdbcType=VARCHAR},


    last_updated_date = now(),


    row_id = #{rowId,jdbcType=VARCHAR},


where area_id = #{areaId,jdbcType=INTEGER}
and last_updated_date = #{lastUpdatedDate,jdbcType=DATE}
and last_updated_by = #{lastUpdatedBy,jdbcType=VARCHAR}
  and goods_id = #{goodsId,jdbcType=INTEGER}
and last_updated_date = #{lastUpdatedDate,jdbcType=DATE}
and last_updated_by = #{lastUpdatedBy,jdbcType=VARCHAR}

update area_goods
set is_active = #{isActive,jdbcType=BIT},
  inactive_date = #{inactiveDate,jdbcType=DATE},
  created_by = #{createdBy,jdbcType=VARCHAR},
  created_date = #{createdDate,jdbcType=DATE},
  last_updated_by = #{lastUpdatedBy,jdbcType=VARCHAR},
  last_updated_date = #{lastUpdatedDate,jdbcType=DATE},
  row_id = #{rowId,jdbcType=VARCHAR}
where area_id = #{areaId,jdbcType=INTEGER}
  and goods_id = #{goodsId,jdbcType=INTEGER}
