mapper.delete:
delete from order
where order_id = #{id,jdbcType=INTEGER}
and last_updated_date = #{lastUpdatedDate,jdbcType=DATE}
and last_updated_by = #{lastUpdatedBy,jdbcType=VARCHAR}

mapper.insert:
insert into order (order_id, order_code, member_id, 
  status, is_rebate, goods_area_id, 
  remarks, created_by, created_date, 
  last_updated_by, last_updated_date, row_id
  )
values (#{orderId,jdbcType=INTEGER}, #{orderCode,jdbcType=VARCHAR}, #{memberId,jdbcType=INTEGER}, 
  #{status,jdbcType=VARCHAR}, #{isRebate,jdbcType=BIT}, #{goodsAreaId,jdbcType=INTEGER}, 
  #{remarks,jdbcType=VARCHAR}, #{createdBy,jdbcType=VARCHAR}, #{createdDate,jdbcType=DATE}, 
  #{lastUpdatedBy,jdbcType=VARCHAR}, #{lastUpdatedDate,jdbcType=DATE}, #{rowId,jdbcType=VARCHAR}
  )
